//Javascript to turn off the preloader when the website has successfully loaded completely


// Create a new <div> element
var div = document.createElement("div");

// Set the id of the <div> element to "preloader"
div.id = "preloader";

// Set the class name of the <div> element to "preloader"
div.className = "preloader";

// Set the HTML content of the <div> element
div.innerHTML = '<div class="black_wall"></div><div class="loader"></div>';

// Insert the <div> element as the first child of the <body> element
document.body.insertBefore(div, document.body.firstChild);

// When the window finishes loading, add the "off" class to the element with the id "preloader"
window.onload = function() {
  document.getElementById("preloader").classList.add("off");
};
